// Actions
const LOAD = "board/LOAD";
const CREATE = "board/CREATE";
const DELETE = "board/DELETE";
const UPDATE = "board/UPDATE";

// const LOADED = "bucket/LOADED";

const initialState = {
  //   list: ["영화관 가기", "매일 책읽기", "수영 배우기"],
  board_list: [
    {
      name: "Jason",
      title: "This place is cool",
      contents: "Nice to meet you everyone!",
      completed: false
    },

    {
      name: "Victoria",
      title: "I like it",
      contents: "Gaesi-pan website is pretty good",
      completed: false
    },

    {
      name: "영수",
      title: "안녕하세요",
      contents: "저는 영수라고 합니다 반가워요~",
      completed: false
    },

    {
      name: "윤아",
      title: "게시판 마음에 들어요^^",
      contents: "앞으로 자주 이용할게요!",
      completed: false
    },

    {
      name: "kevin",
      title: "I'm home!",
      contents: "alone~",
      completed: false
    },
  ],
};

// Action Creators
export const loadBoard = (board) => {
  return { type: LOAD, board };
};

export const createBoard = (board) => {
  return { type: CREATE, board };
};

export const deleteBoard = (board) => {
  return { type: DELETE, board };
};

export const updateBoard = (board) => {
  return { type: UPDATE, board };
};

// export const isLoaded = (loaded) => {
//   return {type: LOADED, loaded};
// }

// Reducer
export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    // do reducer stuff
    case "board/LOAD": { 
        if (action.board.length > 0) {
            return { list: action.board};
        }
        return state;
    }

    case "board/CREATE": {
      const new_board_list = [action.board, ...state.board_list];
      return { ...state, board_list: new_board_list};
    }

    case "board/DELETE": {
      const boardlist = state.board_list.filter((l, idx) => {
        if (idx !== action.board) {
          return l;
        }
      });
      return { board_list: boardlist };
    }

    case "board/UPDATE": {
      const boardlist = state.board_list.map((l, idx) => {
        if (idx === action.board) {

          return { ...l, completed: true };
        }

        return l;
      });

      return { board_list: boardlist };
    }

    // case "bucket/LOADED": {
    //   return {...state, is_loaded: action.loaded};
    // }

    default:
      return state;
  }
}
