// Actions
const LOAD = "board/LOAD";
const CREATE = "board/CREATE";
const DELETE = "board/DELETE";

// const UPDATE = "bucket/UPDATE";

// const LOADED = "bucket/LOADED";

const initialState = {
  //   list: ["영화관 가기", "매일 책읽기", "수영 배우기"],
  board_list: [
    {
    //   number: "1",
      name: "피터팬",
      title: "팅커벨은 어디있어?",
      contents: "제발 돌아와줘"
    },

    {
    //   number: "2",
      name: "라모스",
      title: "나는 레알의 전설",
      contents: "챔스의 사나이"
    },

  ],
};

// Action Creators
export const loadBoard = (board) => {
  return { type: LOAD, board };
};

export const createBoard = (board) => {
  return { type: CREATE, board };
};

export const deleteBoard = (board) => {
  return { type: DELETE, board };
};

// export const updateBucket = (bucket) => {
//   return { type: UPDATE, bucket };
// };

// export const isLoaded = (loaded) => {
//   return {type: LOADED, loaded};
// }

// Reducer
export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    // do reducer stuff
    case "board/LOAD": { 
        if (action.board.length > 0) {
            return { list: action.board };
        }
        return state;
    }

    case "board/CREATE": {
      const new_board_list = [...state.board_list, action.board];
      return { ...state, board_list: new_board_list };
    }

    case "board/DELETE": {
      const boardlist = state.board_list.filter((l, idx) => {
        if (idx !== action.board) {
          return l;
        }
      });
      return { board_list: boardlist };
    }

    // case "bucket/UPDATE": {
    //   const bucket_list = state.list.map((l, idx) => {
    //     if (idx === action.bucket) {

    //       return { ...l, completed: true };
    //     }

    //     return l;
    //   });

    //   return { list: bucket_list };
    // }

    // case "bucket/LOADED": {
    //   return {...state, is_loaded: action.loaded};
    // }

    default:
      return state;
  }
}
